using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Order;

namespace BenchMark;

[MemoryDiagnoser]
[Orderer(SummaryOrderPolicy.FastestToSlowest)]
[RankColumn]
public class D6BM
{
    private readonly string inputs = "";

    private int GetIndexMarkerGeneral(string input, int width)
    {
        return input.TakeWhile((c, i) => input[i..(i + width)].Distinct().Count() != width).Count() + width;
    }

    [Benchmark]
    public void TestFor4() => GetIndexMarkerGeneral(inputs, 4);

    [Benchmark]
    public void TestFor14() => GetIndexMarkerGeneral(inputs, 14);
}
