int GetPriorityValue(char itemType)
{
    int t = (int)itemType % 32;
    if (char.IsUpper(itemType))
        t += 26;
    return t;
}

int CalculatePriorities(string rucksack)
{
    var comcompartment1 = rucksack.Take(rucksack.Length / 2);
    var comcompartment2 = rucksack.Skip(rucksack.Length / 2);

    var communs = comcompartment1.Intersect(comcompartment2);

    return communs.Sum(c => GetPriorityValue(c));
}

var result1 = inputs.Split("\r\n").Select(r => CalculatePriorities(r)).Sum();

Console.WriteLine(result1);

int CalculateBadgePriorities(string[] elfGroup)
{
    var badges = elfGroup[2].Intersect(elfGroup[1]).Intersect(elfGroup[0]);

    return badges.Sum(c => GetPriorityValue(c));
}


