string inputs = "";

int GetIndexMarker(string input)
{
    var chars = input.TakeWhile((c, i) => input[i..(i + 4)].Distinct().Count() != 4);
    return chars.Count() + 4;
}

int GetIndexMarkerV2(string input)
{
    var chars = input.TakeWhile((c, i) => input[i..(i+14)].Distinct().Count() != 14);
    return chars.Count() + 14;
}

int GetIndexMarkerGeneral(string input, int width)
{
    return input.TakeWhile((c, i) => input[i..(i + width)].Distinct().Count() != width).Count() + width;
}

Console.WriteLine($"{nameof(inputs1)} => {GetIndexMarker(inputs1)}");
